import React from 'react';
import ReactDOM from 'react-dom/client';
import './style.scss';
import { useState } from "react";
import reportWebVitals from './reportWebVitals';




function Quiz() {
function ChangeClass() {
  const [hidden, setHidden] = useState("hidden");
  return (
    <div className="quiz-app">
      <h1 className='headline'>Вікторина</h1>
      <ul>Яке місто є столицею України?
        <button className="" onClick={() => setHidden("")}>Київ</button>
        <h2 className={`${hidden}`}>Вірно!</h2>
        <button className='b'>Анкара</button>
        <button className='c'>Хокайдо</button>
        <button className='d'>Янґон</button>
      </ul>
      <ul>Де живе Ілон Маск?
        <button className='a'>В Преторії</button>
        <button className='b'>В Лос-Анджелесі</button>
        <h2 className='hidden'>Вірно!</h2>
        <button className='c'>В Полтаві</button>
        <button className='d'>В Вінніпезі</button>
      </ul>
      <ul>Де вперше було помічено спалах коронавірусу?
        <button className='a'>У місті Бидгощ, Польща</button>
        <button className='b'>У місті Нью-Йорк, США</button>
        <button className='c'>У місті Усурійськ, РФ</button>
        <button className='d'>У місці Ухань, КНР</button>
        <h2 className='hidden'>Вірно!</h2>
      </ul>
      <ul>Коли було зафіксовано перший спалах коронавірусу?
        <button className='a'> Початок 2019-го року</button>
        <button className='b'> Кінець 2019-го року</button>
        <h2 className='hidden'>Вірно!</h2>
        <button className='c'>Початок 2020-го року</button>
        <button className='d'>Кінець 2020-го року</button>
      </ul>
      <ul>Яка страва є традиційною на День подяки в США?
        <button className='a'>Морозиво</button>
        <button className='b'>Індичка</button>
        <h2 className='hidden'>Вірно!</h2>
        <button className='c'>Торт</button>
        <button className='d'>Рисове рагу</button>
      </ul>
      <button className='submit' type='submit'>Завершити</button>
    </div>
  );
}
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Quiz />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
